WINDOWNAME:Brána-Bez DL.Vèera:
INPUT:DATE:USR_DATE:Datum Prùjezdu:Value/Now:CustomFormat/dd.MM.yyyy:
INPUT:CHECKBOX:USR_CHBTVD:Bez vozidel TVD/PGI:Checked/True:
INPUT:BUTTON:USR_BUTTON:ODESLAT:Enabled/True:Submit/True:
SQL:SELECT dg.name AS Brána,rgm.document_nr as 'È.Dod.L.', dc.name AS Dopravce, CASE WHEN dmt.movement_type = 'ingoing' THEN 'Pøíjezd' ELSE 'Odjezd' END AS [Smìr Jízdy], rgm.movement_date AS Datum, rgm.movement_time AS Èas, rgm.number_plate AS SPZ, rgm.carrier_name AS Øidiè, rgm.destination AS [Cíl Cesty], mp.[image] AS Obrázek FROM [DBNAME].[CMP_PORTER].dbo.record_gateway_movement rgm INNER JOIN [DBNAME].[CMP_PORTER].dbo.dial_gateway dg ON rgm.gateway_id = dg.id INNER JOIN [DBNAME].[CMP_PORTER].dbo.dial_carrier dc ON rgm.carrier_id = dc.id INNER JOIN [DBNAME].[CMP_PORTER].dbo.dial_movement_type dmt ON rgm.movement_type_id = dmt.id INNER JOIN [DBNAME].[CMP_PORTER].dbo.movement_pictures mp ON rgm.id = mp.record_id WHERE (rgm.document_nr = '' OR (rgm.document_nr <> '' AND (ISNULL((SELECT TOP 1 1 FROM [DNBAME].dba.pl_zahlavi pl_zah WHERE (CHARINDEX(','+REPLACE(pl_zah.dl,' ','')+',',','+rgm.document_nr+',') <> 0)AND pl_zah.dl <>''), 0) = 0))) AND (rgm.[status] = 'active') AND (rgm.movement_date = CONVERT(DATE,*USR_DATE*,105)) AND (ISNULL ((SELECT 1 AS usr_check1 FROM [DBNAME].[CMP_PORTER].dbo.record_gateway_movement AS par_table WHERE (par_table.parent_rec_id = rgm.id) AND (par_table.document_nr = '' OR (par_table.document_nr <> '' AND (ISNULL((SELECT TOP 1 1 FROM [DNBAME].dba.pl_zahlavi pl_zah2 WHERE (CHARINDEX(','+REPLACE(pl_zah2.dl,' ','')+',',','+par_table.document_nr+',') <> 0) AND pl_zah2.dl <>''), 0) = 0)))), 0) = 0) AND (ISNULL((SELECT 1 AS usr_check2 FROM [DBNAME].[CMP_PORTER].dbo.record_gateway_movement AS par_table2 WHERE (rgm.parent_rec_id = par_table2.id) AND (par_table2.document_nr = '' OR (par_table2.document_nr <> '' AND (ISNULL((SELECT TOP 1 1 FROM [DNBAME].dba.pl_zahlavi pl_zah1 WHERE (CHARINDEX(','+REPLACE(pl_zah1.dl,' ','')+',',','+par_table2.document_nr+',') <> 0) AND pl_zah1.dl <>''), 0) = 0)))), 0) = 0) AND (CHARINDEX(','+dc.name+',',',TVD,PGI,') = 0 OR (ISNULL((*USR_CHBTVD*),0) = 0))
REPORT:Sestava 100:CrystalReport1.rpt:param_2/21.12.2015;new_param/Dnes;
